import React from 'react'
import PropTypes from 'prop-types'
import styles from './SimComponent.scss'
import {PersonEmoji} from '../../VizHelpers';


export default (props) => {


const testPrint = () => {
  console.log(props);
  let emojis = [];
  [...Array(1000).keys()].forEach(i => {
    console.log(i);
    emojis.push(Person("normal","population",Math.floor(i/30),i%30))
  })
  // [...Array(1000).keys()].map(i => console.log(i))

  return emojis;
  // 🛌🛏🤢💀🤒
}

const Person = ({state,location,x,y}) => {
  console.log(x,y);
  return <g key={x*1000+y} x={x} y={y} className="arc">
    {/* <rect width="10" height="110" style="fill:rgb(0,0,0);stroke-width:1;stroke:rgb(0,0,0)" /> */}
    <rect width="10" height="10" />
    <text
      textAnchor="middle"
      alignmentBaseline="middle"
      fill="black"
      fontSize="10"
    >
      {PersonEmoji(state)}
    </text>
  </g>
}

return (
  <svg className="SimComponent" width={props.width} height={props.height}>
    <g>
      {testPrint()}
    </g>
  </svg>
);
}
